{
    "statement": "SELECT RAW users FROM system:completed_requests  WHERE node = NODE_NAME()      AND IFMISSINGORNULL(clientContextID, '') NOT LIKE 'INTERNAL-%'     AND UPPER(IFMISSING(preparedText, statement)) NOT LIKE 'INFER %'     AND UPPER(IFMISSING(preparedText, statement)) NOT LIKE 'ADVISE %'     AND UPPER(IFMISSING(preparedText, statement)) NOT LIKE 'CREATE INDEX%'     AND UPPER(IFMISSING(preparedText, statement)) NOT LIKE 'ALTER INDEX%'     AND UPPER(IFMISSING(preparedText, statement)) NOT LIKE '% SYSTEM:%' GROUP BY users ORDER BY LOWER(users) ASC",    
    "readonly": true,
    "client_context_id": "grafana"
}
